			} else {
				$this->db->query("INSERT INTO " . DB_PREFIX . "contacts_unsubscribe SET send_id = '0', customer_id = '" . (int)$customer_id . "', email = '" . $this->db->escape(utf8_strtolower($data['email'])) . "', date_added = NOW()");
